SELECT HISTORY_ID,CAR_ID, date_format(START_DATE, '%Y-%m-%d') START_DATE,date_format(END_DATE, '%Y-%m-%d') END_DATE, IF (TIMESTAMPDIFF(day,START_DATE,END_DATE) < 29, '단기 대여','장기 대여') RENT_TYPE  FROM CAR_RENTAL_COMPANY_RENTAL_HISTORY WHERE date_format(START_DATE,'%Y%m') = '202209' ORDER BY HISTORY_ID DESC